def shuffle_combinations(words: list, result: str, combinations: list):
    if not words:
        if result not in combinations:
            combinations.append(result)
        return

    for idx, word in enumerate(words):

        shuffle_combinations(words[:idx] + words[idx + 1:], result + word, combinations)


def solution(s: str, words: list):
    combinations = []
    shuffle_combinations(words, "", combinations)

    indexes = []
    for idx, c in enumerate(s):
        for comb in combinations:
            if comb[0] == c:
                if s[idx: idx + len(comb)] == comb:
                    indexes.append(idx)

    return indexes


